import type { ComponentProps, ValidComponent } from "solid-js";
import { createMemo, For, splitProps } from "solid-js";
import type { VariantProps } from "cva";

import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/seperator";
import { cva, cx } from "@/utils/utils";

function FieldSet<T extends ValidComponent = "fieldset">(
	props: ComponentProps<T>,
) {
	const [, rest] = splitProps(props as ComponentProps<"fieldset">, ["class"]);

	return (
		<fieldset
			data-slot="field-set"
			class={cx(
				"flex flex-col gap-6",
				"has-[>[data-slot=checkbox-group]]:gap-3 has-[>[data-slot=radio-group]]:gap-3",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

export type FieldLegendProps<T extends ValidComponent = "legend"> =
	ComponentProps<T> & { variant?: "legend" | "label" };

function FieldLegend<T extends ValidComponent = "legend">(
	props: FieldLegendProps<T>,
) {
	const [, rest] = splitProps(props as FieldLegendProps, ["class", "variant"]);

	return (
		<legend
			data-slot="field-legend"
			data-variant={props.variant ?? "legend"}
			class={cx(
				"mb-3 font-medium",
				"data-[variant=legend]:text-base",
				"data-[variant=label]:text-sm",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

function FieldGroup<T extends ValidComponent = "div">(
	props: ComponentProps<T>,
) {
	const [, rest] = splitProps(props as ComponentProps<"div">, ["class"]);

	return (
		<div
			data-slot="field-group"
			class={cx(
				"group/field-group @container/field-group flex w-full flex-col gap-7 data-[slot=checkbox-group]:gap-3 [&>[data-slot=field-group]]:gap-4",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

export const fieldVariants = cva({
	base: ["group/field flex w-full gap-3 data-[invalid=true]:text-destructive"],
	variants: {
		orientation: {
			vertical: ["flex-col [&>*]:w-full [&>.sr-only]:w-auto"],
			horizontal: [
				"flex-row items-center",
				"[&>[data-slot=field-label]]:flex-auto",
				"has-[>[data-slot=field-content]]:items-start has-[>[data-slot=field-content]]:[&>[role=checkbox],[role=radio]]:mt-px",
			],
			responsive: [
				"flex-col [&>*]:w-full [&>.sr-only]:w-auto @md/field-group:flex-row @md/field-group:items-center @md/field-group:[&>*]:w-auto",
				"@md/field-group:[&>[data-slot=field-label]]:flex-auto",
				"@md/field-group:has-[>[data-slot=field-content]]:items-start @md/field-group:has-[>[data-slot=field-content]]:[&>[role=checkbox],[role=radio]]:mt-px",
			],
		},
	},
	defaultVariants: {
		orientation: "vertical",
	},
});

export type FieldProps<T extends ValidComponent = "div"> = ComponentProps<T> &
	VariantProps<typeof fieldVariants>;

function Field<T extends ValidComponent = "div">(props: FieldProps<T>) {
	const [, rest] = splitProps(props as FieldProps, ["class", "orientation"]);

	return (
		<div
			role="group"
			data-slot="field"
			data-orientation={props.orientation ?? "vertical"}
			class={cx(fieldVariants({ orientation: props.orientation }), props.class)}
			{...(rest as any)}
		/>
	);
}

function FieldContent<T extends ValidComponent = "div">(
	props: ComponentProps<T>,
) {
	const [, rest] = splitProps(props as ComponentProps<"div">, ["class"]);

	return (
		<div
			data-slot="field-content"
			class={cx(
				"group/field-content flex flex-1 flex-col gap-1.5 leading-snug",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

function FieldLabel<T extends ValidComponent = "label">(
	props: ComponentProps<typeof Label<T>>,
) {
	const [, rest] = splitProps(props as ComponentProps<typeof Label>, ["class"]);

	return (
		<Label
			data-slot="field-label"
			class={cx(
				"group/field-label peer/field-label flex w-fit gap-2 leading-snug group-data-[disabled=true]/field:opacity-50",
				"has-[>[data-slot=field]]:w-full has-[>[data-slot=field]]:flex-col has-[>[data-slot=field]]:rounded-md has-[>[data-slot=field]]:border [&>*]:data-[slot=field]:p-4",
				"has-data-[state=checked]:bg-primary/5 has-data-[state=checked]:border-primary dark:has-data-[state=checked]:bg-primary/10",
				"group-data-[invalid=true]/field:text-destructive",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

function FieldTitle<T extends ValidComponent = "div">(
	props: ComponentProps<T>,
) {
	const [, rest] = splitProps(props as ComponentProps<"div">, ["class"]);

	return (
		<div
			data-slot="field-label"
			class={cx(
				"flex w-fit items-center gap-2 text-sm leading-snug font-medium group-data-[disabled=true]/field:opacity-50",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

function FieldDescription<T extends ValidComponent = "p">(
	props: ComponentProps<T>,
) {
	const [, rest] = splitProps(props as ComponentProps<"p">, ["class"]);

	return (
		<p
			data-slot="field-description"
			class={cx(
				"text-muted-foreground text-sm leading-normal font-normal group-has-[[data-orientation=horizontal]]/field:text-balance",
				"last:mt-0 nth-last-2:-mt-1 [[data-variant=legend]+&]:-mt-1.5",
				"[&>a:hover]:text-primary [&>a]:underline [&>a]:underline-offset-4",
				props.class,
			)}
			{...(rest as any)}
		/>
	);
}

function FieldSeparator<T extends ValidComponent = "div">(
	props: ComponentProps<T> & {
		children?: unknown;
	},
) {
	const [, rest] = splitProps(props as ComponentProps<"div">, [
		"class",
		"children",
	]);

	return (
		<div
			data-slot="field-separator"
			data-content={!!props.children}
			class={cx(
				"relative -my-2 h-5 text-sm group-data-[variant=outline]/field-group:-mb-2",
				props.class,
			)}
			{...(rest as any)}
		>
			<Separator class="absolute inset-0 top-1/2" />
			{props.children && (
				<span
					class="bg-background text-muted-foreground relative mx-auto block w-fit px-2"
					data-slot="field-separator-content"
				>
					{props.children}
				</span>
			)}
		</div>
	);
}

export type FieldErrorProps<T extends ValidComponent = "div"> =
	ComponentProps<T> & {
		errors?: Array<{ message?: string } | undefined>;
	};

function FieldError<T extends ValidComponent = "div">(
	props: FieldErrorProps<T>,
) {
	const [, rest] = splitProps(props as FieldErrorProps, [
		"class",
		"children",
		"errors",
	]);

	const content = createMemo(() => {
		if (props.children) {
			return props.children;
		}

		if (!props.errors) {
			return null;
		}

		if (props.errors?.length === 1 && props.errors[0]?.message) {
			return props.errors[0].message;
		}

		return (
			<ul class="ml-4 flex list-disc flex-col gap-1">
				<For each={props.errors}>
					{(error) => error?.message && <li>{error.message}</li>}
				</For>
			</ul>
		);
	});

	if (!content()) {
		return null;
	}

	return (
		<div
			role="alert"
			data-slot="field-error"
			class={cx("text-destructive text-sm font-normal", props.class)}
			{...(rest as any)}
		>
			{content()}
		</div>
	);
}

export {
	Field,
	FieldLabel,
	FieldDescription,
	FieldError,
	FieldGroup,
	FieldLegend,
	FieldSeparator,
	FieldSet,
	FieldContent,
	FieldTitle,
};

import { createFormHook } from "@tanstack/solid-form";
import type { VariantProps } from "cva";
import type { ComponentProps, JSX, ValidComponent } from "solid-js";
import {
	createContext,
	createMemo,
	Show,
	splitProps,
	useContext,
} from "solid-js";

import { Button, type buttonVariants } from "@/components/ui/button";
import {
	Field as DefaultField,
	FieldError as DefaultFieldError,
	FieldSet as DefaultFieldSet,
	FieldContent,
	FieldDescription,
	FieldGroup,
	FieldLabel,
	FieldLegend,
	FieldSeparator,
	FieldTitle,
	type fieldVariants,
} from "@/components/ui/field";
import {
	InputGroup,
	InputGroupAddon,
	InputGroupInput,
} from "@/components/ui/input-group";
import { Spinner } from "@/components/ui/spinner";
import {
	_useFieldContext,
	fieldContext,
	formContext,
	useFormContext,
} from "@/components/ui/tanstack-form-contexts";
import { cx } from "@/utils/utils";

type FormItemContextValue = {
	id: string;
};

const FormItemContext = createContext<FormItemContextValue>();

function FieldSet<T extends ValidComponent = "fieldset">(
	props: ComponentProps<T> & { children?: JSX.Element },
) {
	const id = crypto.randomUUID();
	const [, rest] = splitProps(props as ComponentProps<"fieldset">, [
		"class",
		"children",
	]);

	return (
		<FormItemContext.Provider value={{ id }}>
			<DefaultFieldSet class={cx("grid gap-1", props.class)} {...rest}>
				{props.children}
			</DefaultFieldSet>
		</FormItemContext.Provider>
	);
}

const useFieldContext = () => {
	const formItemContext = useContext(FormItemContext);
	if (!formItemContext) {
		throw new Error("useFieldContext should be used within <FormItem>");
	}

	const { id } = formItemContext;
	const fieldAccessor = _useFieldContext();

	if (!fieldAccessor) {
		throw new Error("useFieldContext should be used within <FormItem>");
	}

	return {
		id,
		name: () => fieldAccessor().name,
		formItemId: `${id}-form-item`,
		formDescriptionId: `${id}-form-item-description`,
		formMessageId: `${id}-form-item-message`,
		errors: () => fieldAccessor().state.meta.errors,
		isTouched: () => fieldAccessor().state.meta.isTouched,
		store: () => fieldAccessor().store,
		field: fieldAccessor,
	};
};

export type FieldProps<T extends ValidComponent = "div"> = ComponentProps<T> &
	VariantProps<typeof fieldVariants> & { children?: JSX.Element };

function Field<T extends ValidComponent = "div">(props: FieldProps<T>) {
	const [, rest] = splitProps(props as FieldProps, [
		"class",
		"orientation",
		"children",
	]);

	const fieldContextValue = useFieldContext();
	const field = fieldContextValue.field();

	const hasVisibleErrors = createMemo(() => {
		const errors = fieldContextValue.errors();
		const isTouched = fieldContextValue.isTouched();
		return errors.length > 0 && isTouched;
	});

	return (
		<DefaultField
			data-invalid={hasVisibleErrors() ? "true" : undefined}
			id={fieldContextValue.formItemId}
			onBlur={field.handleBlur}
			aria-describedby={
				!hasVisibleErrors()
					? `${fieldContextValue.formDescriptionId}`
					: `${fieldContextValue.formDescriptionId} ${fieldContextValue.formMessageId}`
			}
			aria-invalid={hasVisibleErrors()}
			orientation={props.orientation}
			class={props.class}
			{...rest}
		>
			{props.children}
		</DefaultField>
	);
}

function FieldError<T extends ValidComponent = "p">(props: ComponentProps<T>) {
	const [, rest] = splitProps(props as ComponentProps<"p">, ["class"]);

	const fieldContextValue = useFieldContext();

	const errorMessage = createMemo(() => {
		const errors = fieldContextValue.errors();
		const isTouched = fieldContextValue.isTouched();
		if (errors.length === 0 || !isTouched) return null;
		return String(errors[0]?.message ?? "");
	});

	return (
		<Show when={errorMessage()}>
			{(msg) => (
				<DefaultFieldError
					data-slot="form-message"
					id={fieldContextValue.formMessageId}
					class={cx("text-destructive text-sm", props.class)}
					errors={[{ message: msg() }]}
					{...rest}
				/>
			)}
		</Show>
	);
}

function Form<T extends ValidComponent = "form">(
	props: Omit<ComponentProps<T>, "onSubmit" | "noValidate"> & {
		children?: JSX.Element;
	},
) {
	const [, rest] = splitProps(props as ComponentProps<"form">, [
		"class",
		"children",
	]);

	const form = useFormContext();

	const handleSubmit = (e: Event) => {
		e.preventDefault();
		e.stopPropagation();
		form.handleSubmit();
	};

	return (
		<form
			onSubmit={handleSubmit}
			class={cx("flex flex-col p-2 md:p-5 w-full mx-auto gap-2", props.class)}
			noValidate
			{...rest}
		>
			{props.children}
		</form>
	);
}

export type SubmitButtonProps<T extends ValidComponent = "button"> =
	ComponentProps<T> &
		VariantProps<typeof buttonVariants> & {
			label: string;
		};

function SubmitButton<T extends ValidComponent = "button">(
	props: SubmitButtonProps<T>,
) {
	const [, rest] = splitProps(props, ["class", "size", "label"]);

	const form = useFormContext();

	return (
		<form.Subscribe selector={(state) => state.isSubmitting}>
			{(isSubmitting) => (
				<Button
					class={props.class}
					size={props.size}
					type="submit"
					disabled={isSubmitting()}
					{...rest}
				>
					{isSubmitting() && <Spinner />}
					{props.label}
				</Button>
			)}
		</form.Subscribe>
	);
}

export type StepButtonProps<T extends ValidComponent = "button"> =
	ComponentProps<T> &
		VariantProps<typeof buttonVariants> & {
			label: JSX.Element | string;
			handleMovement: () => void;
		};

function StepButton<T extends ValidComponent = "button">(
	props: StepButtonProps<T>,
) {
	const [, rest] = splitProps(props, ["label", "handleMovement"]);

	return (
		<Button
			size="sm"
			variant="ghost"
			type="button"
			onClick={props.handleMovement}
			{...rest}
		>
			{props.label}
		</Button>
	);
}

const { useAppForm, withForm , withFieldGroup } = createFormHook({
	fieldContext,
	formContext,
	fieldComponents: {
		Field,
		FieldError,
		FieldSet,
		FieldContent,
		FieldDescription,
		FieldGroup,
		FieldLabel,
		FieldLegend,
		FieldSeparator,
		FieldTitle,
		InputGroup,
		InputGroupAddon,
		InputGroupInput,
	},
	formComponents: {
		SubmitButton,
		StepButton,
		FieldLegend,
		FieldDescription,
		FieldSeparator,
		Form,
	},
});

export { useAppForm, useFieldContext, useFormContext, withForm ,withFieldGroup};

import { createFormHookContexts } from "@tanstack/solid-form";

export const {
	fieldContext,
	formContext,
	useFieldContext: _useFieldContext,
	useFormContext,
} = createFormHookContexts();
